import React from 'react';
import './NewExpense.css';
import ExpenseForm from './ExpenseForm';

function NewExpense(props){

    const saveExpenseData = (newData) =>{
        const newExpense = {
            ...newData , id:Math.random().toString()
        };
        // console.log(newExpense);
         props.onAddData(newExpense)
    };

    const [showResults, setShowResults] = React.useState(false);
     
    const addNewExpense = () =>{
        setShowResults(true);
     }
    const canceled = () =>{
        // console.log(el)
        setShowResults(false);
    } 

    const button = <button onClick={addNewExpense}>Add New Expense</button>
    const form =  <ExpenseForm onSaveData={saveExpenseData} cancelForms = {canceled}></ExpenseForm>

  

 return(
     <div className="new-expense">
         { showResults ? null : button }
         { showResults ? form : null }
     </div>
 );
}

export default NewExpense;
