import React, { useState } from "react";
import "./ExpenseForm.css";

function ExpenseForm() {
//   const [newTitle, setnewTitle] = useState("");
//   const [newAmount, setnewAmount] = useState("");
//   const [newDate, setnewDate] = useState("");

//   const titleChange = (event) => {
//     setnewTitle(event.target.value);
//   };
//   const amountChange = (event) => {
//     setnewAmount(event.target.value);
//   };
//   const dateChange = (event) => {
//     setnewDate(event.target.value);
//   };

const [userInput , setuserInput] = useState(
    newtitle = '',
    newAmount = '',
    newDate = ''
)
  const titleChange = (event) => {
    setuserInput(
        ...userInput , newTitle = event.target.value);
  };

  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleChange}></input>
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input type="number" onChange={amountChange} min="0.01" step="0.01"></input>
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input type="Date" onChange={dateChange} min="2020-02-02" max="2022-02-02"></input>
        </div>
      </div>
      <div class="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
}

export default ExpenseForm;
