import React from 'react';
import Expense from './Expense';
import ExpenseItem from './ExpenseItem';
import ExpensesFilter from './ExpenseFilter';
import  './ExpenseList.css'

const ExpenseList = (props) =>{
     
    if(props.items.length === 0){
        return(<h2 className = 'expenses-list__fallback'> No Expense Found</h2>)

    }

    // if(props.items.length > 0){
    //   allExpenses = props.items.map((expense) =>  <ExpenseItem key={expense.id} title={expense.title} amount={expense.amount} date={expense.date}/>)
    // }
    return(<ul className="expenses-list">
    {
        props.items.map((expense) =>  <ExpenseItem key={expense.id} title={expense.title} amount={expense.amount} date={expense.date}/>)
    }
    </ul>);

}

export default ExpenseList;
